import React, { useState } from 'react';

const Pairing = ({ students, addPair }) => {
  const [student1, setStudent1] = useState(null);
  const [student2, setStudent2] = useState(null);

  const randomPair = () => {
    const shuffled = [...students].sort(() => Math.random() - 0.5);
    setStudent1(shuffled[0]);
    setStudent2(shuffled[1]);

    addPair(shuffled[0], shuffled[1]);
  };

  return (
    <div>
      <h2>Pair Students</h2>
      <button onClick={randomPair}>Pair Students</button>
      {student1 && student2 && (
        <div>
          <p>Pair: {student1} & {student2}</p>
        </div>
      )}
    </div>
  );
};

export default Pairing;
