import React, { useState } from 'react';

const PairingDashboard = ({ pairedStudents, onAddToHistory }) => {
  const [history, setHistory] = useState([]);

  const handleAddToHistory = () => {
    setHistory([...history, ...pairedStudents]);
    // Optionally, clear pairedStudents or keep them in the dashboard
  };

  return (
    <div>
      <h2>Pairing Dashboard</h2>

      <h3>Paired Students</h3>
      <ul>
        {pairedStudents.map((pair, index) => (
          <li key={index}>{pair[0]} - {pair[1]}</li>
        ))}
      </ul>

      <button onClick={handleAddToHistory}>Add to History</button>

      <h3>Pairing History</h3>
      <ul>
        {history.map((pair, index) => (
          <li key={index}>{pair[0]} - {pair[1]}</li>
        ))}
      </ul>
    </div>
  );
};

export default PairingDashboard;
