import React, { useState } from 'react';

function Pairing() {
  // States for the student list, paired students, and a new student
  const [students, setStudents] = useState([]);  // List of all students
  const [pairedStudents, setPairedStudents] = useState([]); // Paired students
  const [newStudent, setNewStudent] = useState('');  // For adding a new student

  // Function to add a new student to the list
  const addStudent = () => {
    if (newStudent) {
      setStudents([...students, newStudent]);
      setNewStudent('');  // Clear the input after adding
    }
  };

  // Function to randomly pair students
  const pairStudents = () => {
    let shuffledStudents = [...students];
    // Shuffle the students
    for (let i = shuffledStudents.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffledStudents[i], shuffledStudents[j]] = [shuffledStudents[j], shuffledStudents[i]];
    }

    // Pair the shuffled students in pairs
    const pairs = [];
    for (let i = 0; i < shuffledStudents.length; i += 2) {
      pairs.push({ student1: shuffledStudents[i], student2: shuffledStudents[i + 1] });
    }

    // Set the paired students
    setPairedStudents(pairs);
  };

  return (
    <div>
      <h2>Pair Students</h2>
      <div>
        <input 
          type="text" 
          placeholder="Enter student name" 
          value={newStudent} 
          onChange={(e) => setNewStudent(e.target.value)} 
        />
        <button onClick={addStudent}>Add Student</button>
      </div>

      <div>
        <button onClick={pairStudents}>Randomly Pair</button>
      </div>

      <div>
        <h3>Student List</h3>
        <ul>
          {students.map((student, index) => (
            <li key={index}>{student}</li>
          ))}
        </ul>
      </div>

      <div>
        <h3>Paired Students</h3>
        {pairedStudents.length > 0 ? (
          pairedStudents.map((pair, index) => (
            <div key={index}>
              <p>{pair.student1} and {pair.student2}</p>
            </div>
          ))
        ) : (
          <p>No pairs yet.</p>
        )}
      </div>
    </div>
  );
}

export default Pairing;
