import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import HomePage from './components/HomePage';
import PairingDashboard from './components/PairingDashboard';
import Pairing from './components/Pairing';
import UserProfile from './components/UserProfile';

function App() {
  const [pairedStudents, setPairedStudents] = useState([]);  // Paired students
  const [history, setHistory] = useState([]);  // History of paired students

  const handlePairStudents = (students) => {
    setPairedStudents(students);  // Set the paired students
  };

  const handleAddToHistory = (students) => {
    setHistory([...history, ...students]);  // Add paired students to history
  };

  return (
    <div className="app">
      <Header />
      <div className="main-content">
        <Sidebar />
        <div className="content">
          <Routes>
            <Route path="/" exact element={<HomePage />} />
            <Route path="/profile" element={<UserProfile />} />
            <Route path="/pairing" element={<Pairing onPairStudents={handlePairStudents} />} />
            <Route path="/dashboard" element={<PairingDashboard pairedStudents={pairedStudents} onAddToHistory={handleAddToHistory} />} />
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default App;
