import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import Homepage from './components/HomePage';  // Import Homepage
import PairingDashboard from './components/PairingDashboard';
import Pairing from './components/Pairing';
import UserProfile from './components/UserProfile';
import Sidebar from './components/Sidebar';
import Header from './components/Header';

function App() {
  const [user, setUser] = useState(null);  // Manage user state 
  const [pairedStudents, setPairedStudents] = useState([]); // Ensure pairedStudents is initialized as an empty array
  const [history, setHistory] = useState([]); // Manage pairing history

  const addToHistory = (pair) => {
    setHistory([...history, pair]);
  };

  return (
    <div className="app">
        <Header user={user} />
        <div className="main-content">
          <Sidebar />
          <div className="content">
            <Routes>
              <Route path="/" element={<Homepage setUser={setUser} />} />
              <Route path="/profile" element={<UserProfile />} />
              <Route
                path="/pairing"
                element={<Pairing setPairedStudents={setPairedStudents} pairedStudents={pairedStudents} />}
              />
              <Route
                path="/dashboard"
                element={<PairingDashboard pairedStudents={pairedStudents} addToHistory={addToHistory} />}
              />
            </Routes>
          </div>
        </div>
      </div>
  );
}

export default App;
