import React, { useState } from 'react';

function Pairing({ addPairedStudents }) {
  const [studentNames, setStudentNames] = useState([]);
  const [pairingResult, setPairingResult] = useState([]);

  const handleAddStudent = (name) => {
    setStudentNames((prevNames) => [...prevNames, name]);
  };

  const handleRandomPairing = () => {
    const shuffled = [...studentNames].sort(() => Math.random() - 0.5);
    const pairs = [];
    for (let i = 0; i < shuffled.length; i += 2) {
      pairs.push({ student1: shuffled[i], student2: shuffled[i + 1] || 'N/A' });
    }
    setPairingResult(pairs);
    // Update the parent with the paired students
    pairs.forEach((pair) => addPairedStudents(pair));
  };

  return (
    <div>
      <h2>Pair Students</h2>
      <div>
        <input
          type="text"
          placeholder="Enter student name"
          onBlur={(e) => handleAddStudent(e.target.value)}
        />
        <button onClick={handleRandomPairing}>Randomly Pair</button>
      </div>

      <h3>Randomly Paired Students:</h3>
      <ul>
        {pairingResult.map((pair, index) => (
          <li key={index}>
            {pair.student1} and {pair.student2}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Pairing;
