import React, { useState } from 'react';

const Pairing = ({ onAddPair, onPairStudents }) => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState("");

  const handleAddStudent = () => {
    if (newStudent.trim() !== "") {
      setStudents([...students, newStudent]);
      setNewStudent("");  // Clear the input field after adding
    }
  };

  const handlePairStudents = () => {
    if (students.length >= 2) {
      // Shuffle and pair students randomly
      const shuffled = [...students].sort(() => Math.random() - 0.5);
      onPairStudents(shuffled);
      setStudents([]);  // Clear the students list after pairing
    }
  };

  return (
    <div>
      <h2>Pair Students</h2>
      <input
        type="text"
        value={newStudent}
        onChange={(e) => setNewStudent(e.target.value)}
        placeholder="Enter student name"
      />
      <button onClick={handleAddStudent}>Add Student</button>
      <button onClick={handlePairStudents}>Randomly Pair Students</button>

      <h3>Students List</h3>
      <ul>
        {students.map((student, index) => (
          <li key={index}>{student}</li>
        ))}
      </ul>
    </div>
  );
};

export default Pairing;
