import React, { useState } from 'react';

function Pairing() {
  const [students, setStudents] = useState(['Alice', 'Bob', 'Charlie', 'David']);
  const [paired, setPaired] = useState([]);

  const pairStudents = () => {
    const shuffledStudents = [...students].sort(() => Math.random() - 0.5);
    const newPairs = [];
    for (let i = 0; i < shuffledStudents.length; i += 2) {
      newPairs.push({ student1: shuffledStudents[i], student2: shuffledStudents[i + 1] });
    }
    setPaired(newPairs);
  };

  return (
    <div className="pairing">
      <h2>Pair Students</h2>
      <button onClick={pairStudents}>Pair Students</button>
      <ul>
        {paired.map((pair, index) => (
          <li key={index}>
            {pair.student1} & {pair.student2}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Pairing;
