import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import Homepage from './components/HomePage';  // Import Homepage
import PairingDashboard from './components/PairingDashboard';
import Pairing from './components/Pairing';
import UserProfile from './components/UserProfile';
import Sidebar from './components/Sidebar';
import Header from './components/Header';

function App() {
  const [user, setUser] = useState(null);  // Manage user state here

  return (
    <div className="app">
      <Header />
      <div className="main-content">
        <Sidebar />
        <div className="content">
          <Routes>
            <Route path="/" element={<Homepage setUser={setUser} />} /> {/* Pass setUser to Homepage */}
            <Route path="/profile" element={<UserProfile />} />
            <Route path="/pairing" element={<Pairing />} />
            <Route path="/dashboard" element={<PairingDashboard />} />
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default App;
