import React from 'react';

const PairingDashboard = ({ pairedStudents, onAddToHistory }) => {

  const handleAddToHistory = () => {
    onAddToHistory(pairedStudents);  // Add the paired students to history
  };

  return (
    <div>
      <h2>Pairing Dashboard</h2>

      <h3>Paired Students</h3>
      <ul>
        {pairedStudents.map((pair, index) => (
          <li key={index}>
            {pair[0]} - {pair[1]}
          </li>
        ))}
      </ul>

      {/* Button to add the paired students to history */}
      <button onClick={handleAddToHistory}>Add to History</button>
    </div>
  );
};

export default PairingDashboard;
