import React, { useState } from 'react';

function Pairing({ setHistory, setDashboard }) {
  const [students, setStudents] = useState([]);
  const [currentStudent, setCurrentStudent] = useState('');
  const [pairedStudents, setPairedStudents] = useState([]);

  // Function to add a student to the list
  const handleAddStudent = () => {
    if (currentStudent) {
      setStudents([...students, currentStudent]);
      setCurrentStudent('');
    }
  };

  // Function to pair students randomly
  const handlePairStudents = () => {
    if (students.length < 2) {
      alert('At least two students are required to pair.');
      return;
    }

    const shuffledStudents = [...students].sort(() => Math.random() - 0.5);
    const pairs = [];
    for (let i = 0; i < shuffledStudents.length; i += 2) {
      if (shuffledStudents[i + 1]) {
        pairs.push({ student1: shuffledStudents[i], student2: shuffledStudents[i + 1] });
      }
    }

    setPairedStudents(pairs); // Update paired students for this session
    setHistory((prevHistory) => [...prevHistory, ...pairs]); // Add to the history
    setDashboard((prevDashboard) => [...prevDashboard, ...pairs]); // Update the dashboard
  };

  return (
    <div className="pairing">
      <h2>Pair Students</h2>
      <div>
        <input
          type="text"
          value={currentStudent}
          onChange={(e) => setCurrentStudent(e.target.value)}
          placeholder="Enter student name"
        />
        <button onClick={handleAddStudent}>Add Student</button>
      </div>
      <ul>
        {students.map((student, index) => (
          <li key={index}>{student}</li>
        ))}
      </ul>
      <button onClick={handlePairStudents}>Pair Students</button>

      <h3>Paired Students</h3>
      <ul>
        {pairedStudents.map((pair, index) => (
          <li key={index}>
            {pair.student1} & {pair.student2}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Pairing;
