import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import Header from './components/Header'; 
import Sidebar from './components/Sidebar'; 
import Pairing from './components/Pairing'; 
import PairingDashboard from './components/PairingDashboard'; 
import History from './components/History'; 
import UserProfile from './components/UserProfile'; 
import HomePage from './components/HomePage';

function App() {
  const [user, setUser] = useState(null);
  const [pairingHistory, setPairingHistory] = useState([]);
  const [pairedStudents, setPairedStudents] = useState([]);
  const students = ['Alice', 'Bob', 'Charlie', 'David', 'Eve']; // Example students

  const handleLogin = (username) => {
    setUser({ name: username, email: `${username}@example.com` });
  };

  const addPair = (student1, student2) => {
    const newPair = { student1, student2, date: new Date().toLocaleString() };
    setPairingHistory([...pairingHistory, newPair]);
    setPairedStudents([...pairedStudents, newPair]);
  };

  return (
    <div className="app">
      <Header />
      <div className="main-content">
        <Sidebar />
        <div className="content">
          <Routes>
            <Route path="/" element={<HomePage onLogin={handleLogin} />} />
            <Route path="/profile" element={<UserProfile user={user} updateProfile={setUser} />} />
            <Route path="/pairing" element={<Pairing students={students} addPair={addPair} />} />
            <Route path="/dashboard" element={<PairingDashboard pairedStudents={pairedStudents} />} />
            <Route path="/history" element={<History pairingHistory={pairingHistory} />} />
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default App;
