import React, { useState } from 'react';

function PairingDashboard() {
  const [pairedStudents, setPairedStudents] = useState([]);  // Initialize state to an empty array

  // Example of adding a pair
  const addPair = (student1, student2) => {
    setPairedStudents([...pairedStudents, { student1, student2 }]);
  };

  return (
    <div>
      <h2>Paired Students</h2>
      {pairedStudents && pairedStudents.length > 0 ? (
        pairedStudents.map((pair, index) => (
          <div key={index}>
            <p>{pair.student1} and {pair.student2}</p>
          </div>
        ))
      ) : (
        <p>No students paired yet.</p>
      )}

      <button onClick={() => addPair('Alice', 'Bob')}>Add Pair</button>
    </div>
  );
}

export default PairingDashboard;
