import React, { useState } from 'react';

function History() {
  // useState hook with default history data
  const [history, setHistory] = useState([
    { student1: 'Alice', student2: 'Bob', date: '2024-11-01' },
    { student1: 'Charlie', student2: 'David', date: '2024-11-02' },
  ]);

  // Example function to add a new pair to history
  const addPairToHistory = (student1, student2) => {
    const newPair = {
      student1,
      student2,
      date: new Date().toLocaleDateString(),
    };
    setHistory([...history, newPair]); // Update the history state
  };

  return (
    <div className="history">
      <h2>Pairing History</h2>
      <ul>
        {history.map((pair, index) => (
          <li key={index}>
            {pair.student1} & {pair.student2} - {pair.date}
          </li>
        ))}
      </ul>
      <button onClick={() => addPairToHistory('Eve', 'Frank')}>Add Pair</button>
    </div>
  );
}

export default History;
