import React, { useState } from 'react';
import { Route, Routes, BrowserRouter as Router } from 'react-router-dom';
import Homepage from './components/HomePage';  // Import Homepage
import PairingDashboard from './components/PairingDashboard';
import Pairing from './components/Pairing';
import UserProfile from './components/UserProfile';
import Sidebar from './components/Sidebar';
import Header from './components/Header';

function App() {
  const [user, setUser] = useState(null);  // Manage user state here
  const [pairedStudents, setPairedStudents] = useState([]);  // Manage paired students
  const [history, setHistory] = useState([]);  // Manage pairing history

  const addToHistory = (pair) => {
    setHistory([...history, pair]);
  };

  return (
    <Router>
      <div className="app">
        <Header user={user} /> {/* Header shows the user's info */}
        <div className="main-content">
          <Sidebar /> {/* Sidebar handles navigation */}
          <div className="content">
            <Routes>
              <Route path="/" element={<Homepage setUser={setUser} />} /> {/* Pass setUser to Homepage */}
              <Route path="/profile" element={<UserProfile />} />
              <Route 
                path="/pairing" 
                element={<Pairing 
                            setPairedStudents={setPairedStudents} 
                            pairedStudents={pairedStudents}
                          />} 
              />
              <Route 
                path="/dashboard" 
                element={<PairingDashboard 
                            pairedStudents={pairedStudents} 
                            addToHistory={addToHistory} 
                          />} 
              />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
