import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import PairingDashboard from './components/PairingDashboard';
import History from './components/History';
import Login from './components/Login';
import UserProfile from './components/UserProfile';
import Pairing from './components/Pairing';

function App() {
  const [user, setUser] = useState(null);  // Define the user state here
  const [history, setHistory] = useState([]);
  const [dashboard, setDashboard] = useState([]);

  return (
    <div className="app">
      <Header />
      <div className="main-content">
        <Sidebar />
        <div className="content">
          <Routes>
            <Route path="/" exact element={<Login setUser={setUser} />} />
            <Route path="/history" element={<History history={history} />} />
            <Route path="/profile" element={<UserProfile user={user} setUser={setUser} />} />
            <Route path="/pairing" element={<Pairing setHistory={setHistory} setDashboard={setDashboard} />} />
            <Route path="/dashboard" element={<PairingDashboard dashboard={dashboard} />} />
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default App;
