import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login({ setUser }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  // Handle login and set user info
  const handleLogin = () => {
    // Example: simple validation
    if (email && password) {
      // You can replace this with a real login logic
      const loggedInUser = { email };  // Dummy user object for now
      setUser(loggedInUser);  // Set user in the parent component (App.js)
      navigate('/profile');  // Navigate to user profile page
    } else {
      alert('Please enter both email and password.');
    }
  };

  return (
    <div className="login">
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Enter your password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
}

export default Login;
